&lt;ul&gt;
&lt;li style=&quot;font-size: 10pt;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;Features preparation&lt;/span&gt;&lt;/li&gt;
&lt;li style=&quot;font-size: 10pt;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;Model training&lt;/span&gt;&lt;/li&gt;
&lt;li style=&quot;font-size: 10pt;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;API development&lt;/span&gt;&lt;/li&gt;
&lt;li style=&quot;font-size: 10pt;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;Service deployment&lt;/span&gt;&lt;/li&gt;
